mapper.select:

SELECT	spacesNo
FROM spaces
where userNo = ${userNo}

mapper.insert:

		update rooms
   	set thumbImg = #{thumbImg}
   	where roomNo = #{roomNo}

mapper.select:
SELECT /* room (단건) */ r.roomNo, r.spacesNo, r.roomName, r.roomInfo, r.area, r.capacity, r.thumbImg, r.instDtm AS r_instDtm, r.updtDtm AS r_updtDtm,
/* prices (컬렉션) - 모두 p_ 접두사 */ p.pricesNo AS p_pricesNo, p.roomNo AS p_roomNo, p.dayType AS p_dayType, p.startTime AS p_startTime, p.endTime AS p_endTime, p.hourlyPrice AS p_hourlyPrice
FROM rooms r LEFT JOIN roomPrices p ON p.roomNo = r.roomNo WHERE r.roomNo = #{value} ORDER BY p.dayType ASC, p.startTime ASC, p.pricesNo ASC
